package com.example.loading.libjava;

import com.example.loading.libjava.utils.AESUtil;
import com.example.loading.libjava.utils.LogUtil;

public class MyClass {

    private static final String TAG = "MyClass";

    public static void main(String[] args) {
        System.out.println("Hello Java Lib!");

        testAesEncrypt();
//        testStringFormat();
        testStringSplit();
    }

    public static void testAesEncrypt() {
        String key = "123";
        String inputText = "Hello Java Lib";
        String encryptedStr = AESUtil.encrypt(inputText, key);
        String outText = AESUtil.decrypt(encryptedStr, key);
        LogUtil.d(TAG, "-->testAesEncrypt(), inputText=", inputText, ", encryptedStr=", encryptedStr, ", outText=", outText);


        String enStr = "";
        int length = enStr.length();
        String guessKey = SecurityHelper.guessKey(enStr.substring(length - 32));
        String decryptedStr = AESUtil.decrypt(enStr.substring(0, length - 32), guessKey);

        if (LogUtil.enable()) {
            LogUtil.d(TAG, "-->testAesEncrypt(): decryptedStr=", decryptedStr);
        }
    }

    public static void testStringFormat(){
        String format = "%_hello1 %s";
        String result = String.format(format, "World");
        LogUtil.d(TAG, "-->testStringFormat(), result="+result);
    }

    public static void testStringSplit(){
        String initStr = "[Optimize]: 优化guide-launch包代码规范及结构\n\nMIUI-1759861";
        String[] items = initStr.split("\n");
        if(LogUtil.enable()){
            LogUtil.d(TAG, "-->testStringSplit(): items size="+items.length);
        }

    }

}
